    Object.values(audioRefs.current).forEach(a => {
      if (a && a !== audio) {
        a.pause();
        a.currentTime = 0;
      }
    });

    setStatus(prev => ({ ...prev, [id]: 'loading' }));
    setPlayingId(id);
